/* ========================================
  Animation: Shooting Star Border (Non-Tailwind)
  ======================================== */
.button-shooting-star .spark {
  -webkit-mask-image: linear-gradient(white, transparent 50%);
  mask-image: linear-gradient(white, transparent 50%);
}

.button-shooting-star .spark::before {
  content: "";
  position: absolute;
  aspect-ratio: 1 / 1;
  width: 200%;
  background: conic-gradient(from 0deg, transparent 0deg 340deg, white 360deg);
  left: 50%;
  top: -15%;
  transform: translateX(-50%);
  border-radius: 50%;
}

