/* ========================================
   Animation: Button Border Glow (LOOP)
   ======================================== */
@keyframes borderGlowLoop {
  0%,
  100% {
    box-shadow: 0 0 5px #3b82f6;
  }
  50% {
    box-shadow: 0 0 20px #3b82f6;
  }
}
.btn-border-glow {
  border: 2px solid #3b82f6;
  background: transparent;
  animation: borderGlowLoop 2s infinite;
}
