/* ========================================
   Animation: Typewriter (LOOP)
   ======================================== */
@keyframes typewriter {
  0% {
    width: 0;
  }
  50% {
    width: 100%;
  }
  100% {
    width: 0;
  }
}

@keyframes blink {
  0%,
  50%,
  100% {
    border-color: transparent;
  }
  25%,
  75% {
    border-color: white;
  }
}

.animate-typewriter {
  display: inline-block;
  overflow: hidden;
  white-space: nowrap;
  border-right: 2px solid white;
  animation:
    typewriter 4s steps(40) infinite,
    blink 0.75s step-end infinite;
}

