/* ========================================
   Animation: Text Wave
   ======================================== */
@keyframes wave {
  0%,
  60%,
  100% {
    transform: translateY(0);
  }
  30% {
    transform: translateY(-10px);
  }
}

.animate-wave span {
  display: inline-block;
  animation: wave 1.5s infinite;
}

.animate-wave span:nth-child(1) {
  animation-delay: 0s;
}
.animate-wave span:nth-child(2) {
  animation-delay: 0.05s;
}
.animate-wave span:nth-child(3) {
  animation-delay: 0.1s;
}
.animate-wave span:nth-child(4) {
  animation-delay: 0.15s;
}
.animate-wave span:nth-child(5) {
  animation-delay: 0.2s;
}
.animate-wave span:nth-child(6) {
  animation-delay: 0.25s;
}
.animate-wave span:nth-child(7) {
  animation-delay: 0.3s;
}
.animate-wave span:nth-child(8) {
  animation-delay: 0.35s;
}
.animate-wave span:nth-child(9) {
